export const selectOwner = `
SELECT
  "id",
  "mnemonic",
  "encryptionKey"
FROM
  "evolu_owner"
`;

export const createMessageTable = `
CREATE TABLE
  "evolu_message" (
    "timestamp" blob PRIMARY KEY,
    "table" blob,
    "row" blob,
    "column" blob,
    "value" blob
  );
`;

export const createMessageTableIndex = `
CREATE INDEX "index_evolu_message" ON "evolu_message" (
  "table", "row", "column", "timestamp"
);
`;

export const createOwnerTable = `
CREATE TABLE
  "evolu_owner" (
    "id" blob,
    "mnemonic" blob,
    "encryptionKey" blob,
    "timestamp" blob,
    "merkleTree" blob
  );
`;

export const insertOwner = `
INSERT INTO
  "evolu_owner" (
    "id",
    "mnemonic",
    "encryptionKey",
    "timestamp",
    "merkleTree"
  )
VALUES
  (?, ?, ?, ?, ?);
`;

export const selectOwnerTimestampAndMerkleTree = `
SELECT
  "timestamp",
  "merkleTree"
FROM
  "evolu_owner"
`;

export const selectLastTimestampForTableRowColumn = `
SELECT
  "timestamp"
FROM
  "evolu_message"
WHERE
  "table" = ?
  AND "row" = ?
  AND "column" = ?
ORDER BY
  "timestamp" DESC
LIMIT
  1
`;

export const insertValueIntoTableRowColumn = (
  table: string,
  column: string,
): string => `
INSERT INTO
  "${table}" ("id", "${column}")
VALUES
  (?, ?)
ON CONFLICT DO UPDATE SET
  "${column}" = ?
`;

export const insertIntoMessagesIfNew = `
INSERT INTO
  "evolu_message" ("timestamp", "table", "row", "column", "value")
VALUES
  (?, ?, ?, ?, ?)
ON CONFLICT DO NOTHING
`;

export const updateOwnerTimestampAndMerkleTree = `
UPDATE "evolu_owner"
SET
  "timestamp" = ?,
  "merkleTree" = ?
`;

export const selectMessagesToSync = `
SELECT
  *
FROM
  "evolu_message"
WHERE
  "timestamp" >= ?
ORDER BY
  "timestamp"
`;
